mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_shenfen as yonghuShenfen ,yonghu.yonghu_delete as yonghuDelete
FROM xiaofeixiangqing a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.xiaofeixiangqing_name like CONCAT('%',#{params.xiaofeixiangqingName},'%')
and a.xiaofeixiangqing_money >= #{params.xiaofeixiangqingMoneyStart}
and a.xiaofeixiangqing_money <= #{params.xiaofeixiangqingMoneyEnd}
and a.xiaofeixiangqing_money = #{params.xiaofeixiangqingMoney}
and a.xiaofeixiangqing_types = #{params.xiaofeixiangqingTypes}
and ( a.yonghu_id = #{params.yonghuId} )
and a.xiaofeixiangqing_content like CONCAT('%',#{params.xiaofeixiangqingContent},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_shenfen like CONCAT('%',#{params.yonghuShenfen},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
order by a.${params.orderBy} desc
